p = false;        <?php  for ( $i = 0; $i < sizeof($tasks); $i++) { ?>        <?php if (  $tasks[$i]->lat != 0 ) { ?>        showMap = true;        var topos = new google.maps.LatLng(<?php echo $tasks[$i]->lat;  ?>, <?php echo $tasks[$i]->lon;  ?>);        map.setCenter(topos);        <?php if ( $tasks[$i]->status == "COMPLETED" ) { ?>        var imageTo = {            url: '<?php echo base_url(); ?>images/order/pin_done.png',            scaledSize: new google.maps.Size(25, 31)        };        markerTo = new google.maps.Marker({            position: topos,            map: map,            optimized: false,            draggable: false,            icon: imageTo        });        <?php   } else {  ?>        var imageDone = {            url: '<?php echo base_url(); ?>images/order/to_pin.png',            scaledSize: new google.maps.Size(25, 31)        };        markerTo = new google.maps.Marker({            position: topos,            map: map,            optimized: false,            draggable: false,            icon: imageDone            //'http://chart.apis.google.com/chart?chst=d_map_pin_letter&chld=<?php echo $tasks[$i]->sequence;  ?>|FE6256|000000'        });        <?php  } ?>        markers.push(markerTo);        <?php   } ?>        <?php   } ?>        if ( showMap == true ) {            var bounds = new google.maps.LatLngBounds();            for (var i = 0; i < markers.length; i++) {                bounds.extend(markers[i].getPosition());            }            map.fitBounds(bounds);            if (markers.length > 1) {                var bounds = new google.maps.LatLngBounds();                for (var i = 0; i < markers.length; i++) {                    bounds.extend(markers[i].getPosition());                }                map.fitBounds(bounds);            } else {                //var middleBody = $("body").height() / 2;                //var middleMap = parseInt($('#details_box').css("marginTop"))