etUserLocationFromIP();            });        } else {            getUserLocationFromIP();        }    }    function getUserLocationFromIP() {        $.get("https://ipinfo.io", function (response) {            var userLocation = response.loc;            var commaPos = userLocation.indexOf(',');            var userLocLat = parseFloat(userLocation.substring(0, commaPos));            var userLocLon = parseFloat(userLocation.substring(commaPos + 1, userLocation.length));            var pos = new google.maps.LatLng(userLocLat, userLocLon);            mapPin.setCenter(pos);        }, "jsonp");    }    $workerInCenter = false;    function trackorderAjax() {        var orderNumber = '<?php echo $orderNumber; ?>';        jQuery.ajax({            type: "POST",            url: "<?php echo base_url(); ?>o/trackorderAjax/",            dataType: 'json',            data: {                'orderNumber': orderNumber            },            success: function (res) {                $('#title').html(res.title);                $('#subTitle').html(res.subtitle);                if ( res.currentState == 2 ) {                    $('#step2').removeClass();                    $('#step2').addClass("order_icon_completed");                    if ( res.companyPhoneNumber != null ) {                        $("#driverPhoneNumber").attr("href", "tel:" + res.companyPhoneNumber);                        $('#calldriver').removeClass("make_hidden");                    }                }                if ( res.currentState == 3 ) {                    $('#step3').removeClass();                    $('#step3').addClass("order_icon_completed");                    window.location.reload();                }                if ( res.currentState == 4 ) {                    $('#step4').removeClass();                    $('#step4').addClass("order_failed");                }                $scheduledDate = res.scheduledDate;                $scheduledDateText = res.$scheduledDateText;                deleteMarkers();                 for (var  i = 0; i < res.tasks.length; i++) {                     if (  res.tasks[i].locationPinned ) {                            var topos = new google.maps.LatLng(res.tasks[i].lat, res.tasks[i].lon);                        //      map.setCenter(topos);                        // var contentString = res.tasks[i].code;                             if ( res.tasks[i].status == "COMPLETED" ) {                                var imageTo = {                                    url: '<?php echo base_url(); ?>images/order/pin_done.png',                                    scaledSize: new google.maps.Size(25, 31)                                };                                markerTo = new google.maps.Marker({                                    position: topos,                                    map: map,                                    optimized: false,                                    draggable: false,                                    icon: imageTo                                });                             }                             else {                                var imageDone = {                                    url: '<?php echo base_url(); ?>images/order/to_pin.png',                                    scaledSize: new google.maps.Size(25, 31)                                };                                markerTo = new google.maps.Marker({                                    position: topos,                                    map: map,                                    optimized: false,                                    draggable: false,                                    icon: imageDone                                });                             }                         var infowindow = new google.maps.InfoWindow()                         var contentString = "";                         if ( res.tasks[i].userFullName  != null ) {                             contentString = "<b>" +  res.tasks[i].userFullName + "</b>, ";                         }                         if ( res.tasks[i].locality  != null ) {                             contentString =   res.tasks[i].locality + ", ";                         }                         contentString += res.tasks[i].code;;                         google.maps.event.addListener(markerTo,'click', (function(marker,content,infowindow){                             return function() {                                 infowindow.setContent(content);                                 infowindow.open(map,marker);                             };                         })(markerTo,contentString,infowindow));                            markers.push(markerTo);                     }                }                if(driverMarker){                    driverMarker.setMap(null)                }                for (var  i = 0; i < res.driverDetails.length; i++) {                    $canPinLocation = false;                    $('#off_themap').hide();                    $('#map-canvas').show();                    var orderNumber = '<?php echo $orderNumber; ?>';                    var workerUid = res.driverDetails[i].uid;                    var workerName = res.driverDetails[i].driverName;                    var vehicleType = res.driverDetails[i].vehiculeTypeLabel;                 //   if ( res.driverDetails[i].canGetDriverLocation ){                 //   alert( workerName);                    jQuery.ajax({                        type: "POST",                        url: "<?php echo base_url(); ?>o/get_worker_location",                        dataType: 'json',                        data: {                            'workerUid': workerUid,                            'orderNumber': orderNumber                        },                        success: function (res) {                            if (res.lat != null && res.lon != null) {                                var driverpos = new google.maps.LatLng(res.lat, res.lon);                                if ( $workerInCenter == false ) {                                    //     map.setCenter(driverpos);                                }                                if ( vehicleType == "CAR" ){                                    var iconName = 'car.png';                                }                                if ( vehicleType == "MOTORCYCLE" ){                                    var iconName = 'motor.png';                                }                                if ( vehicleType == "TRUCK" ){                                    var iconName = 'truck.png';                                }                                if ( vehicleType == "BICYCLE" ){                                    var iconName = 'bike.png';                                }                                var imageFrom = {                                    url: '<?php echo base_url(); ?>images/worker/' + iconName,                                    origin: new google.maps.Point(0, 0),                                    anchor: new google.maps.Point(25, 25),                                    scaledSize: new google.maps.Size(35, 35)                                };                                driverMarker = new google.maps.Marker({                                    position: driverpos,                                    map: map,                                    optimized: false,                                    draggable: false,                                    icon: imageFrom                                });                                if ( $workerInCenter == false ) {                                    var bounds = new google.maps.LatLngBounds();                                    for (var i = 0; i < markers.length; i++) {                                        bounds.extend(markers[i].getPosition());                                    }                                    bounds.extend(driverMarker.getPosition());                                    $('#map-canvas').css("height","50%");                                    google.maps.event.trigger(map, "resize");                                    map.fitBounds(bounds);                                    //   map.setCenter(driverpos);                                    $workerInCenter = true;                                    map.setOptions({maxZoom: 20});                                }                            } else {                                // window.location.reload();                            }                        },                        error: function (error) {                            //window.location.reload();                        }                    });                 //   }                }            },            error: function (error) {                //alert(error);            }        });    }    var handle = setInterval(trackorderAjax, 5000);    trackorderAjax();    $(function(){        $("#cancelpin_eddress_btn").click(function(){            $('#details_box').show();            $('#off_themap').show();            $('#pin_map').hide();            $("#pin_eddress_btn").hide();            $("#cancelpin_eddress_btn").hide();        });        $("#pin_eddress_btn").click(function(){            //var geocoder = new google.maps.Geocoder;            $("#pin_eddress_btn").hide();            $("#cancelpin_eddress_btn").hide();            var centerloc = mapPin.getCenter();            var lat = centerloc.lat();            var lon = centerloc.lng();            var orderNumber = '<?php echo $orderNumber; ?>';            jQuery.ajax({                type: "POST",                url: "<?php echo base_url(); ?>o/pin_user_location",                dataType: 'json',                data: {                    'lat': lat,                    'lon': lon,                    'orderNumber': orderNumber                },                success: function (res) {                    if (res == 1) {                        window.location.reload();                    } else {                        window.location.reload();                    }                },                error: function (error) {                    window.location.reload();                }            });            return;            //var latlng = {lat: parseFloat(lat), lng: parseFloat(lon)};            $('#details_box').show();            $('#off_themap').show();            $('#pin_map').hide();            geocoder.geocode({'location': latlng}, function(results, status) {                if (status === google.maps.GeocoderStatus.OK) {                    if (results[1]) {                        console.log(results[1]);                        var postal_code = null;                        var street = null;                        var city = null;                        var city2 = null;                        var state = null;                        var country = null;                        $("#postalcodeTextinput").val("");                        $("#streetTextinput").val("");                        $("#cityTextinput").val("");                        $("#stateTextinput").val("");                        $("#countryTextinput").val("");                        $("#phoneTextinput").val("");                        $("#phoneCountryCodeInput").val("");                        $("#countryShortTextinput").val("");                        for (var i=0; i<results[1].address_components.length; i++)                        {                            if (results[1].address_components[i].types[0] == "postal_code") {                                postal_code = results[1].address_components[i];                            }                            if (results[1].address_components[i].types[0] == "neighborhood") {                                street = results[1].address_components[i];                            }                            if (results[1].address_components[i].types[0] == "locality") {                                city = results[1].address_components[i];                            }                            if (results[1].address_components[i].types[0] == "administrative_area_level_1") {                                state = results[1].address_components[i];                            }                            if (results[1].address_components[i].types[0] == "country") {                                country = results[1].address_components[i];                            }                            if (results[1].address_components[i].types[0] == "administrative_area_level_3") {                                city2 = results[1].address_components[i];                            }                        }                        if(postal_code!=null){                            $("#postalcodeTextinput").val(postal_code.long_name);                        }                        if(street!=null){                            $("#streetTextinput").val(street.long_name);                        }                        if(city!=null){                            $("#cityTextinput").val(city.long_name);                        }else if(city2 != null){                            $("#cityTextinput").val(city2.long_name);                        }                        if(state!=null){                            $("#stateTextinput").val(state.long_name);                        }                        if(country!=null){                            $("#countryTextinput").val(country.long_name);                            $("#countryShortTextinput").val(country.short_name);                            $.get('https://restcountries.eu/rest/v1/alpha/'+country.short_name,function (response) {                                $("#phoneTextinput").val("+"+response["callingCodes"]);                                $("#phoneCountryCodeInput").val("+"+response["callingCodes"]);                            });                        }                    } else {//                        window.alert('No results found');                    }                } else {//                    window.alert('Geocoder failed due to: ' + status);                }            });            $("#pin_eddress_btn").hide();            $("#eddress_info_box").show();        });        $("#create_neweddress_btn").click(function(){            var centerloc = mapPin.getCenter();            var lat = centerloc.lat();            var lon = centerloc.lng();            $("#latInput").val(lat);            $("#lonInput").val(lon);            $("#createeddressform").submit();        });        $("#createeddress_closebtn").click(function(){            $("#eddress_info_box").hide();            $("#pin_eddress_btn").show();        });    });    google.maps.event.addDomListener(window, 'load', initialize);</script><div id="page-container" class="page-container">    <?php //if($toEddress->isPinned == 1 || $canGetDriverLocation){ ?>        <div id="map-canvas"></div>    <?php //} ?>    <div id="pin_map" style="display:none; width: 100%; height: 100%;">        <input id="pac-input" class="controls" type="text" placeholder="Search on google maps">        <div id="map_createeddress"></div>        <div class="map_center_pin"></div>        <div class="ping_eddress_btngroup">            <div id="cancelpin_eddress_btn" class="details_blue_btn background_order_btn" style="cursor: pointer">CANCEL</div>            <div id="pin_eddress_btn" class="details_blue_btn background_order_btn" style="cursor: pointer">CONFIRM</div>        </div>    </div>    <div id="sharebox" class="popupbox" style="display: none">        <div id="sharebox_closebtn" class="popupbox_closebtn closepo